import React, { useState, useEffect } from "react";
import Countdown from "react-countdown";

const LandingPage = ({ onStart }) => {
  const [showCountdown, setShowCountdown] = useState(false);

  const targetDate = new Date("July 14, 2028 00:00:00").getTime();

  useEffect(() => {
    // Delay for the GIF to finish playing (e.g., 5 seconds)
    const timer = setTimeout(() => {
      setShowCountdown(true);
    }, 5000); // Adjust to your GIF duration

    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="landing-page">
      {!showCountdown && <div className="gif-background" />}
      {showCountdown && (
        <div className="countdown-container">
          <h1 className="title">LA 2028 Olympics</h1>
          <p className="subtitle">Experience the thrill of the games in Los Angeles!</p>
          <Countdown
            date={targetDate}
            renderer={({ days, hours, minutes, seconds }) => (
              <div className="countdown">
                <span>{days} Days</span> : <span>{hours} Hours</span> :{" "}
                <span>{minutes} Minutes</span> : <span>{seconds} Seconds</span>
              </div>
            )}
          />
          <button className="cta-button" onClick={onStart}>
            Get Started
          </button>
        </div>
      )}
    </div>
  );
};

export default LandingPage;
