// src/Map.js
import React, { useEffect, useState, useRef, useMemo } from "react";
import { MapContainer, TileLayer, Marker, Popup, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import "leaflet-routing-machine";
import L from "leaflet";
import * as turf from "@turf/turf";
import axios from "axios";
import locations from "./locations";

// Import sport icons
import athleticsIcon from "./freebie-olympic-sports-icons/SVG/athletics.svg";
import swimmingIcon from "./freebie-olympic-sports-icons/SVG/swimming.svg";

const createIcon = (iconUrl, zoom) => {
  const size = Math.max(32, zoom * 4);
  return new L.Icon({
    iconUrl,
    iconSize: [size, size],
    iconAnchor: [size / 2, size],
    popupAnchor: [0, -size],
  });
};

function ZoomHandler({ setZoom }) {
  const map = useMap();
  useEffect(() => {
    map.on("zoomend", () => setZoom(map.getZoom()));
  }, [map, setZoom]);
  return null;
}

function RoutingMachine({ start, end, setRouteInfo }) {
  const map = useMap();
  const routingControlRef = useRef(null);

  useEffect(() => {
    if (!start || !end) return;

    if (routingControlRef.current) {
      routingControlRef.current.getPlan().setWaypoints([]);
      map.removeControl(routingControlRef.current);
    }

    routingControlRef.current = L.Routing.control({
      waypoints: [L.latLng(start), L.latLng(end)],
      routeWhileDragging: false,
      lineOptions: { styles: [{ color: "#FF5733", weight: 6 }] },
      createMarker: () => null,
      addWaypoints: false,
      show: false,
    })
      .on("routesfound", (e) => {
        const routes = e.routes;
        setRouteInfo({
          distance: (routes[0].summary.totalDistance / 1000).toFixed(2),
          time: (routes[0].summary.totalTime / 60).toFixed(0),
        });

        // Find POIs
        const line = turf.lineString(
          routes[0].coordinates.map((c) => [c.lng, c.lat])
        );
        const distance = 5 * 1609.34; // 5 miles in meters
        axios
          .get(
            `https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=${start.lat},${start.lng}&radius=${distance}&type=restaurant&key=YOUR_API_KEY`
          )
          .then((res) => {
            res.data.results.forEach((poi) => {
              const point = turf.point([
                poi.geometry.location.lng,
                poi.geometry.location.lat,
              ]);
              if (turf.distance(line, point, { units: "meters" }) <= distance) {
                L.marker([
                  poi.geometry.location.lat,
                  poi.geometry.location.lng,
                ])
                  .addTo(map)
                  .bindPopup(poi.name);
              }
            });
          });
      })
      .addTo(map);

    return () => {
      if (routingControlRef.current) {
        routingControlRef.current.getPlan().setWaypoints([]);
        map.removeControl(routingControlRef.current);
      }
    };
  }, [start, end, map]);

  return null;
}

function Map() {
  const center = [34.0522, -118.2437];
  const [currentZoom, setCurrentZoom] = useState(10);
  const [selectedLocations, setSelectedLocations] = useState([]);
  const [routeInfo, setRouteInfo] = useState(null);

  const dynamicIcons = useMemo(
    () => ({
      athletics: createIcon(athleticsIcon, currentZoom),
      swimming: createIcon(swimmingIcon, currentZoom),
    }),
    [currentZoom]
  );

  const handleMarkerClick = (position) => {
    setSelectedLocations((prev) =>
      prev.length === 2 ? [position] : [...prev, position]
    );
  };

  return (
    <div className="map-container">
      <div className="map-header">
        <h1>Interactive Venue Map</h1>
        <p>Select two locations to calculate a route and discover points of interest.</p>
      </div>
      <div className="map-content">
        <MapContainer
          center={center}
          zoom={10}
          style={{ height: "100%", width: "100%" }}
          zoomAnimation={true}
        >
          <TileLayer
            url="https://{s}.basemaps.cartocdn.com/rastertiles/voyager/{z}/{x}/{y}{r}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />
          <ZoomHandler setZoom={setCurrentZoom} />
          {locations.map((location, idx) => (
            <Marker
              key={idx}
              position={location.position}
              icon={dynamicIcons[location.type] || dynamicIcons.athletics}
              eventHandlers={{
                click: () => handleMarkerClick(location.position),
              }}
            >
              <Popup>
                <strong>{location.name}</strong>
                <br />
                {location.description}
              </Popup>
            </Marker>
          ))}
          {selectedLocations.length === 2 && (
            <RoutingMachine
              start={selectedLocations[0]}
              end={selectedLocations[1]}
              setRouteInfo={setRouteInfo}
            />
          )}
        </MapContainer>
      </div>
      {routeInfo && (
        <div className="route-info">
          <p>
            <strong>Distance:</strong> {routeInfo.distance} km
          </p>
          <p>
            <strong>Estimated Time:</strong> {routeInfo.time} mins
          </p>
        </div>
      )}
    </div>
  );
}

export default Map;
