import React, { useState, useEffect } from "react";
import Countdown from "react-countdown";
import "./landing.css"; // Add custom CSS for animations

const targetDate = new Date("July 14, 2028 00:00:00").getTime();

const LandingPage = ({ onStart }) => {
  const [showCountdown, setShowCountdown] = useState(false); // To control countdown visibility
  const [fadeOut, setFadeOut] = useState(false);

  useEffect(() => {
    // Show the countdown after the GIF finishes playing
    const timer = setTimeout(() => {
      setShowCountdown(true);
    }, 10000); // Adjust this to match the duration of the GIF

    return () => clearTimeout(timer); // Clean up the timeout
  }, []);

  const handleGetStarted = () => {
    setFadeOut(true);
    setTimeout(onStart, 1000); // Wait for the fade-out animation to complete
  };

  return (
    <div className={`landing-page ${fadeOut ? "fade-out" : ""}`}>
      {!showCountdown && <div className="gif-background" />} {/* GIF Background */}
      {showCountdown && (
        <div className="content">
          <img src="/data/ologo.png" alt="Olympics Logo" className="logo" />
          <h1>LA 2028 Olympics</h1>
          <p>Experience the thrill of the games in Los Angeles!</p>
          <div className="countdown">
            <Countdown
              date={targetDate}
              renderer={({ days, hours, minutes, seconds }) => (
                <div className="timer">
                  <div>
                    <span>{days}</span>
                    <small>Days</small>
                  </div>
                  <div>
                    <span>{hours}</span>
                    <small>Hours</small>
                  </div>
                  <div>
                    <span>{minutes}</span>
                    <small>Minutes</small>
                  </div>
                  <div>
                    <span>{seconds}</span>
                    <small>Seconds</small>
                  </div>
                </div>
              )}
            />
          </div>
          <button className="get-started" onClick={handleGetStarted}>
            Get Started
          </button>
        </div>
      )}
    </div>
  );
};

export default LandingPage;
