import React, { useState } from "react";
import LandingPage from "./components/landing.js";
import Dashboard from "./components/dashboard.js";
import Map from "./components/Map.js";
import VenueNavigation from "./components/3.js"; // Assuming the VenueNavigation component is in 3.js
import "./app.css"; // Add CSS for fade-in effect

function App() {
  const [currentPage, setCurrentPage] = useState("landing");

  const handleStart = () => {
    if (currentPage === "landing") setCurrentPage("map");
    else if (currentPage === "map") setCurrentPage("venue");
  };

  const handleBack = () => {
    if (currentPage === "venue") setCurrentPage("map");
    else if (currentPage === "map") setCurrentPage("landing");
  };

  return (
    <>
      {currentPage === "landing" && <LandingPage onStart={handleStart} />}
      {currentPage === "map" && (
        <div className="fade-in">
          <Map />
          <button className="next-button" onClick={handleStart}>
            Next
          </button>
        </div>
      )}
      {currentPage === "venue" && (
        <div className="fade-in">
          <VenueNavigation />
          <button className="next-button" onClick={handleStart}>
            Next
          </button>
        </div>
      )}
    </>
  );
}

export default App;
