import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import "leaflet-routing-machine"; // Import Leaflet Routing Machine
import L from "leaflet";
import locations from "./locations";

// Function to create an icon with dynamic size
const createIcon = (iconUrl, zoom) => {
  const size = Math.max(32, zoom * 4);
  return new L.Icon({
    iconUrl,
    iconSize: [size, size],
    iconAnchor: [size / 2, size],
    popupAnchor: [0, -size],
  });
};

// Component for Routing Control
function RoutingControl({ routePoints }) {
  const map = useMap();

  useEffect(() => {
    const routingControl = L.Routing.control({
      waypoints: routePoints.map((point) => L.latLng(point[0], point[1])),
      routeWhileDragging: true,
      createMarker: () => null, // Disable default markers
      show: false, // Disable default instructions
      lineOptions: {
        styles: [{ color: "blue", weight: 5 }], // Customize the route line
      },
    }).addTo(map);

    return () => {
      if (map.hasLayer(routingControl)) {
        map.removeControl(routingControl); // Safely remove the routing control
      }
    };
  }, [map, routePoints]);

  return null;
}

function Map() {
  const center = [34.0522, -118.2437];
  const [currentZoom, setCurrentZoom] = useState(10);

  const dynamicIcons = {
    athletics: createIcon("/icons/athletics.svg", currentZoom),
    swimming: createIcon("/icons/swimming.svg", currentZoom),
    // Add more icons here...
  };

  const routePoints = locations.map((location) => location.position);

  return (
    <div className="map-container">
      <header className="map-header">
        <h1>Interactive Venue Map</h1>
        <p>Explore the routes and key locations for the 2028 Olympics!</p>
      </header>
      <div className="map-content">
        <MapContainer
          center={center}
          zoom={10}
          style={{ height: "100%", width: "100%" }}
          zoomAnimation={true}
          minZoom={9}
          maxZoom={18}
        >
          <TileLayer
            url="https://{s}.basemaps.cartocdn.com/rastertiles/voyager/{z}/{x}/{y}{r}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors &copy; <a href="https://carto.com/attributions">CARTO</a>'
          />
          <RoutingControl routePoints={routePoints} />
          {locations.map((location, idx) => (
            <Marker
              key={idx}
              position={location.position}
              icon={dynamicIcons[location.type] || dynamicIcons.athletics}
            >
              <Popup>
                <strong>{location.name}</strong>
                <br />
                {location.description}
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>
      <footer className="map-footer">
        <button className="next-button" onClick={() => alert("Go to next page!")}>
          Next
        </button>
      </footer>
    </div>
  );
}

export default Map;
