import React, { useState } from "react";
import Countdown from "react-countdown";
import "./landing.css"; // Add custom CSS for animations

const targetDate = new Date("July 14, 2028 00:00:00").getTime();

const LandingPage = ({ onStart }) => {
  const [showCountdown, setShowCountdown] = useState(false);

  const handleVideoEnd = () => {
    setShowCountdown(true); // Show countdown and button when the video ends
  };

  const handleGetStarted = () => {
    onStart(); // Navigate to the next page
  };

  return (
    <div className="landing-page">
      {!showCountdown && (
        <video
          className="background-video"
          src="/data/your-video.mp4" // Replace with your video file path
          autoPlay
          muted
          playsInline
          onEnded={handleVideoEnd} // Trigger when video ends
        ></video>
      )}
      {showCountdown && (
        <div className="content">
          <h1>LA 2028 Olympics</h1>
          <p>Experience the thrill of the games in Los Angeles!</p>
          <div className="countdown">
            <Countdown
              date={targetDate}
              renderer={({ days, hours, minutes, seconds }) => (
                <div className="timer">
                  <div>
                    <span>{days}</span>
                    <small>Days</small>
                  </div>
                  <div>
                    <span>{hours}</span>
                    <small>Hours</small>
                  </div>
                  <div>
                    <span>{minutes}</span>
                    <small>Minutes</small>
                  </div>
                  <div>
                    <span>{seconds}</span>
                    <small>Seconds</small>
                  </div>
                </div>
              )}
            />
          </div>
          <button className="get-started" onClick={handleGetStarted}>
            Get Started
          </button>
        </div>
      )}
    </div>
  );
};

export default LandingPage;
