import React, { useState } from "react";
import Countdown from "react-countdown";
import "./landing.css"; // Add custom CSS for animations

const targetDate = new Date("July 14, 2028 00:00:00").getTime();

const LandingPage = ({ onStart }) => {
  const [showOverlay, setShowOverlay] = useState(false);

  const handleVideoEnd = () => {
    setShowOverlay(true); // Show countdown and button when the video ends
  };

  const handleGetStarted = () => {
    onStart(); // Navigate to the next page
  };

  return (
    <div className="landing-page">
      {/* Video background */}
      <video
        className={`background-video ${showOverlay ? "freeze" : ""}`}
        src="/data/intro.mov" // Replace with your video file path
        autoPlay
        muted
        playsInline
        onEnded={handleVideoEnd} // Trigger when video ends
      ></video>

      {/* Overlay content */}
      {showOverlay && (
        <div className="overlay">
          <h1 className="title">LA 2028 Olympics</h1>
          <p className="subtitle">Experience the thrill of the games in Los Angeles!</p>
          <div className="countdown">
            <Countdown
              date={targetDate}
              renderer={({ days, hours, minutes, seconds }) => (
                <div className="timer">
                  <div>
                    <span>{days}</span>
                    <small>Days</small>
                  </div>
                  <div>
                    <span>{hours}</span>
                    <small>Hours</small>
                  </div>
                  <div>
                    <span>{minutes}</span>
                    <small>Minutes</small>
                  </div>
                  <div>
                    <span>{seconds}</span>
                    <small>Seconds</small>
                  </div>
                </div>
              )}
            />
          </div>
          <button className="get-started" onClick={handleGetStarted}>
            Get Started
          </button>
        </div>
      )}
    </div>
  );
};

export default LandingPage;
